import React, { useState } from 'react';
import Login from "./Login"
import Customer from "./Customer"


function App() {

  const loggedIn = false;

  return (
    <div>
      <div>My Logo</div>
      { !loggedIn && <Login />}
      { loggedIn && <Customer /> }
    </div>

  );
}

export default App;
