import React, { useState } from 'react';
import './App.css';

function App() {

  //const [name, setName] = useState("linz");
  let name = "linz"

  const changeName = (e) => {
    //setName(e.target.value)
    name = e.target.value;
  }

  return (
    <div className="App">
      <header className="App-header">
       <div>
        <input type="text" value={name} onChange={changeName} />
       </div>
      </header>
    </div>
  );
}

export default App;
